import React, { useEffect, useState } from 'react'
import {useNavigate,useParams} from 'react-router-dom'
import axios from 'axios'

function EditPost() {
    const { id } = useParams();
    const [description,setDescription] = useState('');
    const [reference,setReference] = useState('');
    const [image,setImage] = useState(null);
    const navigate = useNavigate();

    const handleImage = (e)=>{
        setImage(e.target.files[0]);
    }

    useEffect(()=>{
        const fetchData = async()=>{
            try{
                const response = await axios.get(`http://localhost:5000/post/${id}`);
                console.log(response.data.post);
                setImage(response.data.post.image);
                setDescription(response.data.post.description);
                setReference(response.data.post.reference);
            }
            catch(err){
                console.log(err);
            }
        }
        fetchData();
    },[]);

    const handleSubmit = async(e) =>{
        e.preventDefault();
        const formData = new F;
        formData.append('description',description);
        formData.append('reference',reference);
        formData.append('postImage',image);
        try{
            const response = await axios.post(`http://localhost:5000/edit/${id}`,formData,{
                headers:{
                    'Content-Type': 'multipart/form-data'
                }
            })
            console.log(response.data);
        }
        catch(err){
            console.log(err);
        }
    }
    return (
        <div>
            <div>
        <form onSubmit={handleSubmit}>
            <input 
                type='text'
                value={description}
                onChange={(e)=>{setDescription(e.target.value)}}
            />
             <input 
                type='url'
                value={reference}
                onChange={(e)=>{setReference(e.target.value)}}
            />
             <input 
                type='file'
                onChange={handleImage}
            />
            <button type='submit'>Submit</button>
        </form>
    </div>
        </div>
    );
}

export default EditPost;
