import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import Navbar from './Navbar';
import '../css/AluRegister.css'; // Make sure to import your custom CSS file

const AluRegister = () => {
  const [username, setUsername] = useState('');
  const [email, setEmail] = useState('');
  const [batch, setBatch] = useState('');
  const [dob, setDob] = useState('');
  const [password, setPassword] = useState('');
  const [profileImage, setProfileImage] = useState(null); // Added state for image
  const [error, setError] = useState('');
  const [message, setMessage] = useState('');
  const navigate = useNavigate();

  const handleImageChange = (e) => {
    setProfileImage(e.target.files[0]);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    // Clear previous messages
    setError('');
    setMessage('');

    // Validate data
    if (!username || !email || !batch || !dob || !password) {
      setError('All fields are required.');
      return;
    }

    const formData = new FormData();
    formData.append('username', username);
    formData.append('email', email);
    formData.append('batch', batch);
    formData.append('dob', dob);
    formData.append('password', password);
    if (profileImage) {
      formData.append('profileImage', profileImage);
    }

    try {
      const response = await axios.post('http://localhost:5000/register', formData, {
        headers: {
          'Content-Type': 'multipart/form-data'
        }
      });

      setMessage(response.data.message);
      navigate('/alumnilogin');
    } catch (err) {
      // Display error message from server
      if (err.response && err.response.data && err.response.data.error) {
        setError(err.response.data.error);
      } else {
        setError('An error occurred. Please try again.');
      }
    }
  };

  return (
    <>
      <Navbar />
      <div className='alu-register-container'>
        <h2 className='alu-register-title'>Register</h2>
        <form onSubmit={handleSubmit} className='alu-register-form'>
          <div className='alu-input-group'>
            <label>Username:</label>
            <input
              type="text"
              value={username}
              onChange={(e) => setUsername(e.target.value)}
              className='alu-input'
            />
          </div>
          <div className='alu-input-group'>
            <label>Email:</label>
            <input
              type="email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              className='alu-input'
            />
          </div>
          <div className='alu-input-group'>
            <label>Batch:</label>
            <input
              type="text"
              value={batch}
              onChange={(e) => setBatch(e.target.value)}
              className='alu-input'
            />
          </div>
          <div className='alu-input-group'>
            <label>Date of Birth:</label>
            <input
              type="date"
              value={dob}
              onChange={(e) => setDob(e.target.value)}
              className='alu-input'
            />
          </div>
          <div className='alu-input-group'>
            <label>Password:</label>
            <input
              type="password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              className='alu-input'
            />
          </div>
          <div className='alu-input-group'>
            <label>Profile Image:</label>
            <input
              type="file"
              onChange={handleImageChange}
              className='alu-input-file'
            />
          </div>
          <button type="submit" className='alu-submit-button'>Register</button>
          {message && <p className='alu-message'>{message}</p>}
          {error && <p className='alu-error'>{error}</p>}
        </form>
      </div>
    </>
  );
};

export default AluRegister;
