import React, { useState, useEffect } from "react";
import { Button, Alert } from "react-bootstrap";
import axios from "axios";
import { useNavigate } from "react-router-dom";

// Utility function to get cookie by name
const getCookie = (name) => {
    const value = `; ${document.cookie}`;
    const parts = value.split(`; ${name}=`);
    if (parts.length === 2) return parts.pop().split(';').shift();
    return null;
};

const Dashboard = () => {
    const [message, setMessage] = useState("");
    const [username, setUsername] = useState("");
    const navigate = useNavigate();

    useEffect(() => {
        // Fetch user data from backend (using token from cookies)
        const fetchUserData = async () => {
            try {
                const token = getCookie('token');
                
                const response = await axios.get("http://localhost:5000/dashboard", {
                    headers: {
                        Authorization: `Bearer ${token}` // Use token retrieved from cookies
                    }
                });
                
                if (response.data.username) {
                    setUsername(response.data.username); // Set the username from the backend
                } else {
                    setMessage("Please log in to view the dashboard.");
                    navigate('/login'); // Redirect to login if not logged in
                }
            } catch (error) {
                setMessage("An error occurred while fetching user data.");
                console.error("Error:", error);
                navigate('/login'); // Redirect to login if there's an error
            }
        };

        fetchUserData();
    }, [navigate]);

    const handleLogout = async () => {
        try {
            await axios.post("http://localhost:5000/logout", {}, {
                headers: {
                    Authorization: `Bearer ${getCookie('token')}` // Include token for logout request if needed
                }
            });
            // Clear the cookie or token if necessary
            document.cookie = "token=; expires=Thu, 01 Jan 1970 00:00:00 UTC; path=/;"; // Example of clearing cookie
            navigate('/login'); // Redirect to login page after logout
        } catch (error) {
            setMessage("Logout failed. Please try again.");
            console.error("Logout error:", error);
        }
    };

    return (
        <div className="container mt-5">
            <h2>Welcome to your Dashboard, {username}</h2>
            {message && (
                <Alert variant="danger" onClose={() => setMessage("")} dismissible>
                    {message}
                </Alert>
            )}

            <Button variant="danger" onClick={handleLogout}>
                Logout
            </Button>
        </div>
    );
};

export default Dashboard;
