import React from 'react'
import Navbar from './Navbar'
import '../css/Option.css'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faGraduationCap } from '@fortawesome/free-solid-svg-icons';
import { faChalkboardUser } from '@fortawesome/free-solid-svg-icons';
import { faUser } from '@fortawesome/free-solid-svg-icons';
import { useNavigate } from 'react-router-dom';



function Options() {
    const navigate = useNavigate();
    const handleAlumni = () =>{
        useNavigate('/alumniregister');
    }
    const handleStudent = () =>{
        useNavigate('/studentregister');
    }
    const handleStaff = () =>{
        useNavigate('/staffregister')
    }
  return (
    <div>
        <Navbar/>
        <div className='option'>
            <div className='box' onClick={handleAlumni}>
                <FontAwesomeIcon icon={faGraduationCap} className='icon'/>
                <p>Alumni</p>
            </div>
            <div className='box' onClick={handleStaff}>
                <FontAwesomeIcon icon={faChalkboardUser} className='icon'/>
                <p>Staff</p>
            </div>
            <div className='box'>
                <FontAwesomeIcon icon={faUser} className='icon'/>
                <p>Student</p>
            </div>

        </div>
    </div>
  )
}

export default Options
