import React, { useEffect, useState } from 'react';
import '../css/AluProfile.css'; // Import the CSS for the profile card
import Navbar from './Navbar';
import { useNavigate } from 'react-router-dom';
import axios from 'axios'

const AluProfile = () => {
  const [Details,setDetails] = useState('');
  const navigate = useNavigate();

  useEffect(()=>{
    const fetchDetails= async() =>{
      const token = localStorage.getItem('token');
      if(!token){
        navigate('/alumnilogin');
      }
      try{
        
        const response = await axios.get('http://localhost:5000/')

      }
      catch(err){
        console.log(err);
      }
    }
  },[])

  return (
    <>
    <Navbar/>
    <div className="profile-card">
      <div className="profile-header">
        <img
          src="https://randomuser.me/api/portraits/women/75.jpg" // Placeholder image
          alt="Profile"
          className="profile-img"
        />
      </div>
      <div className="profile-body">
        <h3>Samantha Jones</h3>
        <p>New York, United States</p>
        <p>Web Producer - Web Specialist</p>
        <p>Columbia University - New York</p>

        <div className="profile-stats">
  
          <div>
            <h4>43</h4>
            <p>Posts</p>
          </div>
        </div>

        <button className="profile-button">Show more</button>
      </div>
    </div>
    </>
  );
};

export default AluProfile;
