import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';


const AluDashBoard = () => {
    const [posts, setPosts] = useState([]);
    const navigate = useNavigate();

    useEffect(() => {
        const fetchPosts = async () => {
            const token = localStorage.getItem('token');
            if (!token) {
                navigate('/login');
                return;
            }

            try {
                const response = await axios.get('http://localhost:5000/my-posts', {
                    headers: {
                        'Authorization': `Bearer ${token}`
                    }
                });
                setPosts(response.data.posts || []);
            } catch (err) {
                console.error(err);
                navigate('/login');
            }
        };

        fetchPosts();
    }, [navigate]);

    const handleAddPost = () => {
        navigate('/add-post');
    };

    return (
        <>
        <div className="dashboard-container">
            <div className="dashboard-header">
                <h2>Dashboard</h2>
                <button className="add-post-button" onClick={handleAddPost}>Add Post</button>
            </div>
            <ul className="posts-list">
                {posts.map(post => (
                    <li key={post._id} className="post-item">
                        <img src={`http://localhost:5000/public/posts/${post.image}`} alt={post.description} />
                        <div>
                            <p>{post.description}</p>
                            {post.reference && <p><a href={post.reference}>{post.reference}</a></p>}
                        </div>
                    </li>
                ))}
            </ul>
        </div>
        </>
    );
};

export default AluDashBoard;
