import axios from 'axios';
import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import '../css/components/Post.css'

function Post() {
    const [post, setPost] = useState(null);
    const { id } = useParams();

    useEffect(() => {
        const fetchPost = async () => {
            try {
                const response = await axios.get(`http://localhost:5000/post/${id}`);
                setPost(response.data.post); // Make sure response.data.post exists
                console.log(response.data);
            } catch (err) {
                console.log(err);
            }
        };
        fetchPost();
    }, [id]);

    if (!post) {
        return <div>Loading...</div>; // Add a loading state while fetching data
    }

    return (
        <div>
            {/* Check if post.image exists */}
            {post.image && (
                <img src={`http://localhost:5000/posts/${post.image}`} alt="Post" />
            )}
            <p>{post.description}</p>
            <p>{post.reference && <a href={post.reference}>{post.reference}</a>}</p>
        </div>
    );
}

export default Post;
