import React, { useEffect, useState } from 'react'
import Navbar1 from './Navbar1'
import axios from 'axios'

function AluPortal() {
    const [data,setData] = useState([]);
    useEffect(() => {
        const fetchData = async () => {
            try {
                const response = await axios.get('http://localhost:5000/alumniPortal');
                console.log(response.data.user);
                setData(response.data.user);
            } catch (err) {
                console.log(err);
            }
        };
        fetchData();
    }, []);
    
  return (
    <div>
        <Navbar1/>
        <h1>Alumni Portal</h1>
        <div className='UserContainer'>
            {data.map((datas)=>(
                <div className='userProfile'>
                    <img src={`http://localhost:5000/images/${datas.profileImage}`}/>
                    <p>username:{datas.username}</p>
                    <p>Email:{datas.email}</p>
                    <p>Batch:{datas.batch}</p>
                    <p>DOB:{datas.dob.getDate()}</p>
                </div>
            ))}
        </div>

    </div>
  )
}

export default AluPortal
