import React, { useEffect, useState } from 'react';
import { useNavigate, useParams } from 'react-router-dom';
import axios from 'axios';

function EditPost() {
    const { id } = useParams();  
    const [description, setDescription] = useState('');  
    const [reference, setReference] = useState(''); 
    const [category,setCategory] = useState(''); 
    const [image, setImage] = useState(null); 
    const [currentImage, setCurrentImage] = useState('');  
    const navigate = useNavigate();  

    
    const handleImage = (e) => {
        setImage(e.target.files[0]); 
    };

    const handleCategory = (e)=>{
        setCategory(e.target.value)
    }

    
    useEffect(() => {
        const fetchData = async () => {
            try {
                const response = await axios.get(`http://localhost:5000/post/${id}`);
                const { image, description, reference } = response.data.post;
                
                setCurrentImage(image);  
                setDescription(description);  
                setReference(reference);  
            } catch (err) {
                console.log(err);
            }
        };
        fetchData();  
    }, [id]);  

    
    const handleSubmit = async (e) => {
        e.preventDefault(); 
        const token = localStorage.getItem('token');
        console.log("Form Submitted"); 
        const formData = new FormData();  
        formData.append('description', description);
        formData.append('reference', reference);
        
        
        if (image) {
            formData.append('postImage', image);  
        }

        try {
            const response = await axios.post(`http://localhost:5000/edit/${id}`, formData, {
                headers: {
                    'Content-Type': 'multipart/form-data',
                    'Authorization':`Bearer ${token}`
                }
            });
            console.log(response.data);
            navigate(`/post/${id}`);  
        } catch (err) {
            console.log(err);
        }
    };

    return (
        <div>
            <form onSubmit={handleSubmit}>
                <input 
                    type='text'
                    value={description}
                    onChange={(e) => setDescription(e.target.value)}
                    placeholder="Description"
                />
                <input 
                    type='url'
                    value={reference}
                    onChange={(e) => setReference(e.target.value)}
                    placeholder="Reference Link"
                />
                
                {currentImage && <img src={`http://localhost:5000/public/posts/${currentImage}`} alt="Current Post" width="200" />}
                <input 
                    type='file'
                    onChange={handleImage}
                />
                <select
                  name="category"
                  value={category}
                  onChange={handleCategory}
                  required
                >
                  <option value="">SelectCategory</option>
                  <option value="Event">Event</option>
                  <option value="News">News</option>
                  <option value="Jobs">Jobs</option>
                  <option value="InternShip">InternShip</option>
                </select>
                <button type='submit'>Submit</button>
            </form>
        </div>
    );
}

export default EditPost;
