import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import Navbar from './Navbar';
import '../css/AluRegister.css'; // Import custom CSS
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'; // Font Awesome
import { faUser, faEnvelope, faGraduationCap, faCalendarAlt, faLock, faImage } from '@fortawesome/free-solid-svg-icons'; // Icons

const AluRegister = () => {
  const [username, setUsername] = useState('');
  const [email, setEmail] = useState('');
  const [batch, setBatch] = useState('');
  const [dob, setDob] = useState('');
  const [password, setPassword] = useState('');
  const [profileImage, setProfileImage] = useState(null); // Added state for image
  const [error, setError] = useState('');
  const [message, setMessage] = useState('');
  const navigate = useNavigate();

  const handleImageChange = (e) => {
    setProfileImage(e.target.files[0]);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    // Clear previous messages
    setError('');
    setMessage('');

    // Validate data
    if (!username || !email || !batch || !dob || !password) {
      setError('All fields are required.');
      return;
    }

    const formData = new FormData();
    formData.append('username', username);
    formData.append('email', email);
    formData.append('batch', batch);
    formData.append('dob', dob);
    formData.append('password', password);
    if (profileImage) {
      formData.append('profileImage', profileImage);
    }

    try {
      const response = await axios.post('http://localhost:5000/register', formData, {
        headers: {
          'Content-Type': 'multipart/form-data'
        }
      });

      setMessage(response.data.message);
      navigate('/alumnilogin');
    } catch (err) {
      if (err.response && err.response.data && err.response.data.error) {
        setError(err.response.data.error);
      } else {
        setError('An error occurred. Please try again.');
      }
    }
  };

  return (
    <>
      <Navbar />
      <div className="alu-register-container">
        <div className="alu-image-section">
          <img src="https://via.placeholder.com/500" alt="Registration Visual" className="alu-register-image" />
        </div>
        <div className="alu-form-section">
          <h2 className="alu-register-title">Register</h2>
          <form onSubmit={handleSubmit} className="alu-register-form">
            <div className="alu-input-group">
              <FontAwesomeIcon icon={faUser} className="alu-input-icon" />
              <input
                type="text"
                placeholder="Username"
                value={username}
                onChange={(e) => setUsername(e.target.value)}
                className="alu-input"
              />
            </div>
            <div className="alu-input-group">
              <FontAwesomeIcon icon={faEnvelope} className="alu-input-icon" />
              <input
                type="email"
                placeholder="Email"
                value={email}
                onChange={(e) => setEmail(e.target.value)}
                className="alu-input"
              />
            </div>
            <div className="alu-input-group">
              <FontAwesomeIcon icon={faGraduationCap} className="alu-input-icon" />
              <input
                type="text"
                placeholder="Batch"
                value={batch}
                onChange={(e) => setBatch(e.target.value)}
                className="alu-input"
              />
            </div>
            <div className="alu-input-group">
              <FontAwesomeIcon icon={faCalendarAlt} className="alu-input-icon" />
              <input
                type="date"
                value={dob}
                onChange={(e) => setDob(e.target.value)}
                className="alu-input"
              />
            </div>
            <div className="alu-input-group">
              <FontAwesomeIcon icon={faLock} className="alu-input-icon" />
              <input
                type="password"
                placeholder="Password"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
                className="alu-input"
              />
            </div>
            <div className="alu-input-group">
              <FontAwesomeIcon icon={faImage} className="alu-input-icon" />
              <input
                type="file"
                onChange={handleImageChange}
                className="alu-input-file"
              />
            </div>
            <button type="submit" className="alu-submit-button">Register</button>
            {message && <p className="alu-message">{message}</p>}
            {error && <p className="alu-error">{error}</p>}
          </form>
        </div>
      </div>
    </>
  );
};

export default AluRegister;
