import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import Navbar from './Navbar';


const AluDashBoard = () => {
    const [Details, setDetails] = useState(null);
    const navigate = useNavigate();

    useEffect(() => {
        const fetchPosts = async () => {
            const token = localStorage.getItem('token');
            if (!token) {
                navigate('/login');
                return;
            }

            try {
                const response = await axios.get('http://localhost:5000/details', {
                    headers: {
                        'Authorization': `Bearer ${token}`
                    }
                });
                console.log(response.data.user);
                setDetails(response.data.user);
            } catch (err) {
                console.error(err);
                navigate('/login');
            }
        };

        fetchPosts();
    }, [navigate]);

    const handleAddPost = () => {
        navigate('/alumniaddpost');
    };

    return (
        <>
        <Navbar/>
        <Container className="d-flex justify-content-center align-items-center vh-100">
      <Card className="text-center shadow-lg p-4">
        <Card.Body>
          <h1 className="mb-3">Welcome, {alumniName}!</h1>
          <p className="lead">We're glad to have you back at the Alumni Association.</p>

          <div className="d-flex justify-content-center mt-4">
            <Link to="/dashboard">
              <Button variant="primary" className="m-2">Go to Dashboard</Button>
            </Link>
            <Link to="/events">
              <Button variant="success" className="m-2">View Events</Button>
            </Link>
            <Link to="/logout">
              <Button variant="danger" className="m-2">Logout</Button>
            </Link>
          </div>
        </Card.Body>
      </Card>
    </Container>
        </>
    );
};

export default AluDashBoard;
