import React, { useEffect, useState } from 'react';
import axios from 'axios';
import 'bootstrap/dist/css/bootstrap.min.css';

const Directory = () => {
  const [alumni, setAlumni] = useState([]);

  useEffect(() => {
    const fetchAlumni = async () => {
      try {
        const response = await axios.get('http://localhost:5000/alumni');
        setAlumni(response.data);
      } catch (error) {
        console.error('Error fetching alumni data:', error);
      }
    };

    fetchAlumni();
  }, []);

  return (
    <div className="container mt-5">
      <h1 className="text-center">Alumni Directory</h1>
      <div className="row">
        {alumni.map((alumnus) => (
          <div className="col-md-4" key={alumnus._id}>
            <div className="card mb-4">
              {/* Display the image if available */}
              {alumnus.image && (
                <img
                  src={`http://localhost:5000/images/${alumnus.image}`}
                  className="card-img-top"
                  alt={`${alumnus.username}'s profile`}
                />
              )}
              <div className="card-body">
                <h5 className="card-title">{alumnus.username}</h5>
                <p className="card-text"><strong>Batch:</strong> {alumnus.batch}</p>
                <button className="btn btn-primary">View</button>
              </div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Directory;
