import React, { useState } from 'react';
import axios from 'axios';
import 'bootstrap/dist/css/bootstrap.min.css'; // Make sure Bootstrap is imported

const Admin = () => {
  const [name, setName] = useState('');
  const [dob, setDob] = useState('');
  const [batch, setBatch] = useState('');
  const [message, setMessage] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const response = await axios.post('http://localhost:5000/admin', {
        name,
        dob,
        batch
      });

      setMessage(response.data.message);
    } catch (error) {
      setMessage('Error registering alumni: ' + error.response.data.error);
    }
  };

  return (
    <div className="container mt-5">
      <h1 className="text-center mb-4">Register New Alumni</h1>
      {message && <div className="alert alert-info">{message}</div>}
      <form onSubmit={handleSubmit} className="border p-4 shadow-sm bg-light">
        <div className="form-group">
          <label htmlFor="name">Name</label>
          <input
            type="text"
            className="form-control"
            id="name"
            value={name}
            onChange={(e) => setName(e.target.value)}
            required
          />
        </div>
        <br />

        <div className="form-group">
          <label htmlFor="dob">Date of Birth</label>
          <input
            type="date"
            className="form-control"
            id="dob"
            value={dob}
            onChange={(e) => setDob(e.target.value)}
            required
          />
        </div>
        <br />

        <div className="form-group">
          <label htmlFor="batch">Batch</label>
          <input
            type="text"
            className="form-control"
            id="batch"
            value={batch}
            onChange={(e) => setBatch(e.target.value)}
            required
          />
        </div>
        <br />

        <button type="submit" className="btn btn-primary btn-block">Register Alumni</button>
      </form>
    </div>
  );
};

export default Admin;
