import React, { useState } from "react";
import axios from "axios";

const DonationForm = () => {
  const [formData, setFormData] = useState({ name: "", email: "", amount: "" });

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handlePayment = async () => {
    try {
      const { data } = await axios.post("http://localhost:5000/api/donation/create-order", formData);

      const options = {
        key: "rzp_test_73Og2QB6bRrFB9", // Replace with your Razorpay Key ID
        amount: data.order.amount,
        currency: data.order.currency,
        name: "Alumni Donation Portal",
        description: "Thank you for your generosity!",
        order_id: data.order.id,
        handler: async (response) => {
          const paymentData = {
            razorpay_order_id: response.razorpay_order_id,
            razorpay_payment_id: response.razorpay_payment_id,
            razorpay_signature: response.razorpay_signature,
          };

          const result = await axios.post(
            "http://localhost:5000/api/donation/verify-payment",
            paymentData
          );
          alert(result.data.message);
        },
        prefill: {
          name: formData.name,
          email: formData.email,
        },
        theme: {
          color: "#528FF0",
        },
      };

      const rzp = new window.Razorpay(options);
      rzp.open();
    } catch (err) {
      console.error(err);
      alert("Something went wrong!");
    }
  };

  return (
    <div>
      <h2>Donation Form</h2>
      <input type="text" name="name" placeholder="Name" onChange={handleChange} />
      <input type="email" name="email" placeholder="Email" onChange={handleChange} />
      <input type="number" name="amount" placeholder="Amount" onChange={handleChange} />
      <button onClick={handlePayment}>Donate</button>
    </div>
  );
};

export default DonationForm;
