import React, { useState, useEffect } from "react";
import { Button, Alert } from "react-bootstrap";
import axios from "axios";
import { useHistory } from "react-router-dom";

const Dashboard = () => {
    const [message, setMessage] = useState("");
    const [username, setUsername] = useState("");
    const history = useHistory();

    useEffect(() => {
        // Fetch user data from backend (for example using token from cookies)
        const fetchUserData = async () => {
            try {
                const response = await axios.get("http://localhost:5000/dashboard");
                if (response.data.username) {
                    setUsername(response.data.username); // Set the username from the backend
                } else {
                    setMessage("Please log in to view the dashboard.");
                    history.push("/login");
                }
            } catch (error) {
                setMessage("An error occurred while fetching user data.");
                console.error("Error:", error);
                history.push("/login");
            }
        };

        fetchUserData();
    }, [history]);

    const handleLogout = () => {
        axios.post("http://localhost:5000/logout").then(() => {
            history.push("/login");
        });
    };

    return (
        <div className="container mt-5">
            <h2>Welcome to your Dashboard, {username}</h2>
            {message && (
                <Alert variant="danger" onClose={() => setMessage("")} dismissible>
                    {message}
                </Alert>
            )}

            <Button variant="danger" onClick={handleLogout}>
                Logout
            </Button>
        </div>
    );
};

export default Dashboard;
