import React, { useState } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import axios from 'axios';

function AlumniRegistrationPage() {
  const [formData, setFormData] = useState({
    fullName: '',
    email: '',
    password: '',
    confirmPassword: '',
    graduationYear: '',
    department: '',
    currentProfession: '',
    currentLocation: '',
    profilePicture: null,
    socialMediaLinks: '',
  });

  const handleChange = (e) => {
    const { id, value, files } = e.target;
    if (files) {
      setFormData({ ...formData, [id]: files[0] });
    } else {
      setFormData({ ...formData, [id]: value });
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    // Validate password and confirm password match
    if (formData.password !== formData.confirmPassword) {
      alert("Passwords do not match!");
      return;
    }

    // Create form data object for sending files
    const formPayload = new FormData();
    Object.keys(formData).forEach((key) => {
      formPayload.append(key, formData[key]);
    });

    try {
      const response = await axios.post('http://localhost:5000/api/register', formPayload, {
        headers: {
          'Content-Type': 'multipart/form-data',
        },
      });

      if (response.status === 200) {
        alert("Registration successful!");
      } else {
        alert("Registration failed. Please try again.");
      }
    } catch (error) {
      console.error("There was an error registering!", error);
    }
  };

  return (
    <div className="container mt-5">
      <div className="row justify-content-center">
        <div className="col-md-8 col-lg-6">
          <div className="card p-4 shadow-sm">
            <h1 className="text-center mb-4">Alumni Registration</h1>
            <h5 className="text-center mb-4">Create your account</h5>
            <form onSubmit={handleSubmit}>
              <div className="form-group">
                <label htmlFor="fullName">Full Name</label>
                <input
                  type="text"
                  id="fullName"
                  className="form-control"
                  placeholder="Enter your full name"
                  value={formData.fullName}
                  onChange={handleChange}
                />
              </div>
              <div className="form-group">
                <label htmlFor="email">Email Address</label>
                <input
                  type="email"
                  id="email"
                  className="form-control"
                  placeholder="Enter your email address"
                  value={formData.email}
                  onChange={handleChange}
                />
              </div>
              <div className="form-row">
                <div className="form-group col-md-6">
                  <label htmlFor="password">Password</label>
                  <input
                    type="password"
                    id="password"
                    className="form-control"
                    placeholder="Enter your password"
                    value={formData.password}
                    onChange={handleChange}
                  />
                </div>
                <div className="form-group col-md-6">
                  <label htmlFor="confirmPassword">Confirm Password</label>
                  <input
                    type="password"
                    id="confirmPassword"
                    className="form-control"
                    placeholder="Confirm your password"
                    value={formData.confirmPassword}
                    onChange={handleChange}
                  />
                </div>
              </div>
              <div className="form-group">
                <label htmlFor="graduationYear">Graduation Year</label>
                <select
                  id="graduationYear"
                  className="form-control"
                  value={formData.graduationYear}
                  onChange={handleChange}
                >
                  <option value="">Select...</option>
                  <option value="2024">2024</option>
                  <option value="2025">2025</option>
                  <option value="2026">2026</option>
                  <option value="2027">2027</option>
                </select>
              </div>
              <div className="form-group">
                <label htmlFor="department">Department/Field of Study</label>
                <select
                  id="department"
                  className="form-control"
                  value={formData.department}
                  onChange={handleChange}
                >
                  <option value="">Select...</option>
                  <option value="cs">Computer Science</option>
                  <option value="ee">Electrical Engineering</option>
                  <option value="me">Mechanical Engineering</option>
                  <option value="ce">Civil Engineering</option>
                  <option value="bio">Biotechnology</option>
                </select>
              </div>
              <div className="form-group">
                <label htmlFor="currentProfession">Current Profession</label>
                <input
                  type="text"
                  id="currentProfession"
                  className="form-control"
                  placeholder="Enter your current profession"
                  value={formData.currentProfession}
                  onChange={handleChange}
                />
              </div>
              <div className="form-group">
                <label htmlFor="currentLocation">Current Location</label>
                <input
                  type="text"
                  id="currentLocation"
                  className="form-control"
                  placeholder="Enter your current location"
                  value={formData.currentLocation}
                  onChange={handleChange}
                />
              </div>
              <div className="form-group">
                <label htmlFor="profilePicture">Upload Profile Picture (optional)</label>
                <input
                  type="file"
                  id="profilePicture"
                  className="form-control-file"
                  onChange={handleChange}
                />
              </div>
              <div className="form-group">
                <label htmlFor="socialMediaLinks">Social Media Links</label>
                <input
                  type="text"
                  id="socialMediaLinks"
                  className="form-control"
                  placeholder="Enter LinkedIn, Facebook, etc."
                  value={formData.socialMediaLinks}
                  onChange={handleChange}
                />
              </div>
              <div className="text-center mt-4">
                <button type="submit" className="btn btn-primary me-2">Submit</button>
                <button type="button" className="btn btn-secondary">Login</button>
              </div>
              <div className="text-center mt-3">
                <small className="text-muted">
                  By registering, you agree to the <a href="#terms">Terms</a> and <a href="#privacy">Privacy Policy</a>.
                </small>
              </div>
            </form>
          </div>
        </div>
      </div>
    </div>
  );
}

export default AlumniRegistrationPage;
