import React, { useEffect, useState } from 'react';
import '../css/AluProfile.css'; 
import Navbar from './Navbar';
import { useNavigate, useParams } from 'react-router-dom';
import axios from 'axios';

const AluProfile = () => {
  const [Details, setDetails] = useState(null); 
  const navigate = useNavigate();
  
  // Destructure 'id' from useParams
  const { id } = useParams(); 

  const handleShow = () => {
    navigate('/alumnidashboard');
  };

  useEffect(() => {
    const fetchDetails = async () => {
      try {
        // Use the destructured id value in the URL
        const response = await axios.get(`http://localhost:5000/profile/${id}`);
        console.log(response.data);
        setDetails(response.data.user); 
      } catch (err) {
        console.log(err);
      }
    };

    fetchDetails();
  }, [id]); 

  if (!Details) {
    return <p>Loading...</p>; 
  }

  const formatDate = (dob) => {
    const date = new Date(dob);
    return date.toISOString().split('T')[0]; // Formats date as YYYY-MM-DD
};

  return (
    <>
      <Navbar />
      <div className="profile-card">
        <div className="profile-header">
          <img
            src={`http://localhost:5000/images/${Details.profileImage}`} 
            alt="Profile"
            className="profile-img"
          />
        </div>
        <div className="profile-body">
          <h3>{Details.username}</h3>
          <p><b>Batch:</b> {Details.batch}</p>
          <p><b>DOB:</b>{formatDate(Details.dob)}</p>
          <p><b>Email:</b> {Details.email}</p>

          <div className="profile-stats">
            <div>
              <h4>{Details.posts.length}</h4>
              <p>Posts</p>
            </div>
          </div>
        </div>
      </div>
    </>
  );
};

export default AluProfile;
