import React, { useState } from 'react'
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import Navbar1 from './Navbar1';
import '../css/StudLogin.css'

function StudLogin() {
    const [username,setUsername] = useState('');
    const [password,setPassword] = useState('');
    const navigate = useNavigate();

    const handleSubmit = async(e)=>{
        e.preventDefault();
        try{
            const response = await axios.post('http://localhost:5000/studentLogin',{username,password});
            console.log(response.data.token);
            if(response.data.token){
                localStorage.setItem('token',response.data.token);
                navigate('/studentDashBoard');
            }
            else{
                console.log(response.data.err);
            }
        }
        catch(err){
            console.log(err);
        }
        

    }

  return (
    <div>
        <Navbar1/>
        <div className='Container'>
            <div className='Container1'>
            <div className='img-Container'>
                <img src='https://th.bing.com/th/id/OIP.ZG-mtqOAnZhuypUgOmzGbgHaE8?w=259&h=180&c=7&r=0&o=5&dpr=1.3&pid=1.7'/>
            </div>
            <div className='form-Container'>
            <h1>Student Login</h1>
            <form onSubmit={handleSubmit}>
                <input 
                type='text'
                value={username}
                onChange={(e)=>setUsername(e.target.value)} 
                required
                />
                <input 
                type='password'
                value={password}
                onChange={(e)=>setPassword(e.target.value)} 
                required
                />
                <button type='submit'>Submit</button>
                <p>Do you Want to <a href='/'>SignUp</a></p>
            </form>
            </div>
            </div>
        </div>
    </div>
  )
}

export default StudLogin
