import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import Navbar from './Navbar';

const AddPost = () => {
    const [description, setDescription] = useState('');
    const [referenceLink, setReferenceLink] = useState('');
    const [image, setImage] = useState(null);
    const [message, setMessage] = useState('');
    const navigate = useNavigate();

    const handleImageChange = (e) => {
        setImage(e.target.files[0]);
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        const formData = new FormData();
        formData.append('description', description);
        formData.append('referenceLink', referenceLink);
        if (image) {
            formData.append('postImage', image); // Ensure this field name matches the server-side Multer configuration
        }

        const token = localStorage.getItem('token');
        if (!token) {
            navigate('/login');
            return;
        }

        try {
            const response = await axios.post('http://localhost:5000/add-post', formData, {
                headers: {
                    'Authorization': `Bearer ${token}`,
                    'Content-Type': 'multipart/form-data'
                }
            });
            setMessage(response.data.message || 'Post added successfully');
            if (!response.data.error) {
                navigate('/alumdashboard');
            }
        } catch (err) {
            console.error(err);
            setMessage('An error occurred');
        }
    };

    return (
        <>
        <Navbar/>
        <div>
            <h2>Add Post</h2>
            <form onSubmit={handleSubmit}>
                <div>
                    <label>Description:</label>
                    <input
                        type="text"
                        value={description}
                        onChange={(e) => setDescription(e.target.value)}
                        required
                    />
                </div>
                <div>
                    <label>Reference Link:</label>
                    <input
                        type="url"
                        value={referenceLink}
                        onChange={(e) => setReferenceLink(e.target.value)}
                    />
                </div>
                <div>
                    <label>Image:</label>
                    <input
                        type="file"
                        onChange={handleImageChange}
                        required
                    />
                </div>
                <button type="submit">Add Post</button>
                {message && <p>{message}</p>}
            </form>
        </div>
        </>
    );
};

export default AddPost;
